sys_script_include.script:
var NeedItUtils = Class.create();
NeedItUtils.prototype = {
    initialize: function() {
    },

	isDatePast: function(strDate){
		// Create GlideDateTime objects for the current date and the passed in date
		var current_datetime = new GlideDateTime();
		var test_datetime = new GlideDateTime(strDate);
		// Get the date portion of current_datetime and test_datetime (no timestamp)
		var current_date = current_datetime.getLocalDate();
		var test_date = test_datetime.getLocalDate();
		// If the test_date is before current_date, return true, else return false
		if (test_date.before(current_date)) {
			return true;
		}
		else {
			return false;
		}
	},

	isDateToday: function(strDate){
		// Create GlideDateTime objects for the current date and the passed in date
		var current_datetime = new GlideDateTime();
		var test_datetime = new GlideDateTime(strDate);
		// Get the date portion of current_datetime and test_datetime (no timestamp)
		var current_date = current_datetime.getLocalDate();
		var test_date = test_datetime.getLocalDate();		
		// If the test_date is equal to current_date, return true, else return false
		if (test_date.compareTo(current_date) == 0) {
			return true;
		}
		else {
			return false;
		}
	},

    type: 'NeedItUtils'
};
